 function graficarRaices(a,b,c,X)      Y = a*X.^2 + b*X + c ;    r = raices(a, b ,c);        L1 = ['y = ' num2str(a) 'x^2 + ' num2str(b) 'x + ' num2str(c)];    if length(r) == 2        hold on            grid minor            raiz1 = num2str(r(1))            raiz2 = num2str(r(2))            plot(X, Y, '-b');            plot(r(1), 0,'r+');            plot(r(2), 0,'g+');                        legend(L1, ["x1= ", raiz1], ["x2= ", raiz2]);        hold off            elseif length(r) == 1        hold on            grid minor            raiz1 = num2str(r(1));            plot(X, Y, '-b');            plot(r(1), 0,'r+');                        legend(L1, ["x= ", raiz1]) ;        hold off                       else        hold on                        grid minor            plot(X, Y, '-b');            legend(L1);        hold off    
 end
