% Busca raíces de ecuaciones cuadraticasfunction r=raices(a,b,c)        d = (b^2) - (4*a*c);    % Discriminante     if a == 0        warning('ecuacion incorrecta')        r = []; % sin soluciones            elseif d == 0        r = [-b/2*a]; % 1 solucion            elseif d < 0         warning('Raíces Complejas')        r = []; % sin soluciones    else        r = [(-b + sqrt(d))/2*a , (-b - sqrt(d))/2*a];                endif        endfunction