X= [0.0:0.5:10.0]; %[inicio: paso: fin] n=length(X);Y= rand(1,n)*100;  % vector de 21 números aleatorios (pues hay 21 digitos en X) entre 1 y 100sumaY= sum(Y);     % suma en YsumaX= sum(X);     % suma en XsumaXY= sum(X.*Y);  % suma ponderada de x*ysumaCuadradosX= sum(X.^2);sumaCubosX= sum(X.^3);sumaCuarticaX= sum(X.^4);sumaponderadaX2Y= sum((X.^2).*Y);A=[sumaX   , n      ;  %Matriz A (2x2)   sumaCuadradosX, sumaX];      B= [sumaY;             %Matriz B (1x2)    sumaXY];C= inv(A)*B; % Acá se soluciona el sistema para m y k (las soluciones quedan en una matriz vertical 1x2 de tipo [m;k])m=C(1); % m es el término (1,1)k=C(2); % k es el termino (1,2)y1= m*X+k; % Recta de aproximación del conjunto de puntos (X,Y)%% Ahora, se intenta hacer la misma regresion, pero en vez de lineal; cuadrática.%% Se resuelve el siguiente sistema para encontrar los coeficientes de y2= a*X.^2+ b*X+ c %% a ΣX^2 + b ΣX   + c n      = ΣY%% a ΣX^3 + b ΣX^2 + c ΣX     = ΣXY%% a ΣX^4 + b ΣX^3 + c ΣX^2   = ΣX^2YD= [sumaCuadradosX,          sumaX,                n ;              % D es la matríz de la parte antes de la igualdad    sumaCubosX,     sumaCuadradosX,             sumaX;    sumaCuarticaX,      sumaCubosX,    sumaCuadradosX];E= [sumaY;                                                          % E es la matriz de los coeficientes libres    sumaXY;    sumaponderadaX2Y];    F= inv(D)*E;     %Se resuelve el segundo sistema para a, b y c    a=F(1);b=F(2);c=F(3);    y2= a*X.^2 + b*X + c; %Se genera la parábola con a, b y c encontradoshold onplot(X,Y, '*'); %puntitosplot(X, y1,'+-'); %rectaplot(X, y2, '+-'); %parabolahold offL1=['y1= ',num2str(m),'x + ',num2str(k)];       L2=['y2= ',num2str(a),'x^2 + ',num2str(b),'x + ',num2str(c)]; % Un clásico: "lle es igual a a equis cuadrado más be equis más ce" legend('puntos',L1,L2)xlabel('X')ylabel('Y')title('Aproximar puntos por recta y1 y parabola y2')        