X = -180 : 10 : 180;SenX= sind(X);CosX= cosd(X);PSenX = polyfit(X, SenX, 3); %Coeficientes de aproximacionesPCosX = polyfit(X, CosX, 4);Y3 = polyval(PSenX,X);      %Funciones de aproximacionY4 = polyval(PCosX, X);hold onplot(X,SenX, 'r+'); % puntitos Seno en rojoplot(X, CosX, 'b+'); % puntitios Coseno en Azulplot(X,Y3, 'r'); % Aproximación cubica Seno en rojoplot(X,Y4, 'b'); % Aproximación cubica Seno en rojoS= ['4 Coeficientes Seno: ' num2str(PSenX)];C= ['5 Coeficientes Coeno: ' num2str(PCosX)];legend('Seno', 'Coseno', S, C);plot([0,0],[-1,1],'k-'); % Dibujo Eje Yplot([min(X), max(X)],[0,0],'k-'); % Dibujo Eje Xgrid minor xlabel('Angulo en Grados');title('Aproximacion de seno y coseno con polinomios de grado 3 y 4');A = regexp(PSenX,' ','split')hold off