X=0:10:180;Y=sind(X);n = length(X);sumaY= sum(Y);     % suma en YsumaX= sum(X);     % suma en XsumaXY= sum(X.*Y);  % suma ponderada de x*ysumaCuadradosX= sum(X.^2);sumaCubosX= sum(X.^3);sumaCuarticaX= sum(X.^4);sumaponderadaX2Y= sum((X.^2).*Y);%% Ahora, se intenta hacer la misma regresion, pero en vez de lineal; cuadrática.%% Se resuelve el siguiente sistema para encontrar los coeficientes de y2= a*X.^2+ b*X+ c %% a ΣX^2 + b ΣX   + c n      = ΣY%% a ΣX^3 + b ΣX^2 + c ΣX     = ΣXY%% a ΣX^4 + b ΣX^3 + c ΣX^2   = ΣX^2YD= [sumaCuadradosX,          sumaX,                n ;              % D es la matríz de la parte antes de la igualdad    sumaCubosX,     sumaCuadradosX,             sumaX;    sumaCuarticaX,      sumaCubosX,    sumaCuadradosX];E= [sumaY;                                                          % E es la matriz de los coeficientes libres    sumaXY;    sumaponderadaX2Y];    F= inv(D)*E;     %Se resuelve el segundo sistema para a, b y c    a=F(1);b=F(2);c=F(3);    y2= a*X.^2 + b*X + c; %Se genera la parábola con a, b y c encontradoshold onplot(X,Y, '*'); %puntitosplot(X, y2, '+-'); %parabolahold offL1=['y2= ',num2str(a),'x^2 + ',num2str(b),'x + ',num2str(c)]; legend('puntos',L1);xlabel('Angulo en Grados');ylabel('Seno');title('Funcion seno y parabola que lo aproxima');grid on