pulgadas=[70,63,72,60,66,70,74,65,62,67,65,68];libras=[155,150,180,135,156,168,178,160,132,145,139,152];edad=[19,17,18,16,18,18,20,17,18,19,16,18];metros= pulgadas*0.0254;kilos = libras*0.45;PKilosRMetros = polyfit(metros, kilos, 1); %Coeficientes de aproximacion kilos respecto a metrosKilosRMetros = polyval(PKilosRMetros, metros); %Recta de aproximacion kilos respecto a metrosPMetrosREdad = polyfit(edad, metros, 1); %Coeficientes de aproximacion metros respecto a edadMetrosREdad = polyval(PMetrosREdad, edad); %Recta de aproximacion metros respecto a edadpromedio1 = mean(KilosRMetros);promedio2 = mean(MetrosREdad);%fig 1 kilos respecto a metroshold ongrid minorplot(metros , kilos, 'r+'); % puntitos metros en rojoplot(metros,  KilosRMetros, 'b'); % Aproximación kilo respecto a metroplot([min(metros), max(metros)], [promedio1, promedio1],'g-'); % Dibujo promedio kilosR1=[num2str(PKilosRMetros(1)), '*Metros + ', num2str(PKilosRMetros(2))];legend('Kilos', R1, 'Promedio Kilos');xlabel('Metros');ylabel('Kilos');title('Alturas y pesos de personas');hold off%fig.2 metros respecto a edadfigurehold ongrid minorplot(edad , metros, 'r+'); % puntitos edad en rojoplot(edad,  MetrosREdad, 'b'); % Aproximación metro respecto a edadplot([min(edad), max(edad)], [promedio2, promedio2],'g-'); % Dibujo promedio kilosR1=[num2str(PMetrosREdad(1)), '*Edad + ', num2str(PMetrosREdad(2))];legend('Metros', R1, 'Promedio Metros');ylabel('Metros');xlabel('Edad');title('Edades y Alturas de personas');hold off