%Calcula correlacion lineal (R) entre dos conjuntos de datitos uwufunction R=correlacion(X,Y)  n=length(X);  sumaY= sum(Y);     % suma en Y sumaX= sum(X);     % suma en X sumaXY= sum(X.*Y);  % suma ponderada de x*y sumaCuadradosX= sum(X.^2); % suma de los cuadrados de x sumaCuadradosY= sum(Y.^2); % suma de los cuadrados de x R= (n*sumaXY-(sumaX.*sumaY))/(((n*sumaCuadradosX-(sumaX.^2)).*(n*sumaCuadradosY-(sumaY.^2))).^(0.5))  endfunction 